#!/usr/bin/env python3\nimport sys\nsys.path.append('/app')\n\nfrom app.database import SessionLocal, init_database\nfrom app.models.user import User, UserRole\nfrom sqlalchemy.orm import Session\nimport bcrypt\n\ndef simple_hash_password(password: str) -> str:\n    \"\"\"Simple password hashing using bcrypt directly\"\"\"\n    return bcrypt.hashpw(password.encode('utf-8'), bcrypt.gensalt()).decode('utf-8')\n\ndef create_users():\n    print(\"Creating users...\")\n    \n    # Initialize database\n    init_database()\n    \n    db = SessionLocal()\n    try:\n        # Create admin user\n        admin_user = db.query(User).filter(User.email == \"admin@bank.com\").first()\n        if not admin_user:\n            admin_user = User(\n                email=\"admin@bank.com\",\n                password=simple_hash_password(\"admin123\"),\n                name=\"Admin User\",\n                role=UserRole.admin,\n                is_active=True,\n                kyc_status=\"verified\"\n            )\n            db.add(admin_user)\n            print(\"Admin user created\")\n        \n        # Create regular user\n        user = db.query(User).filter(User.email == \"user@bank.com\").first()\n        if not user:\n            user = User(\n                email=\"user@bank.com\",\n                password=simple_hash_password(\"user123\"),\n                name=\"Regular User\",\n                role=UserRole.user,\n                is_active=True,\n                kyc_status=\"verified\"\n            )\n            db.add(user)\n            print(\"Regular user created\")\n        \n        # Create test user\n        test_user = db.query(User).filter(User.email == \"test@test.com\").first()\n        if not test_user:\n            test_user = User(\n                email=\"test@test.com\",\n                password=simple_hash_password(\"test123\"),\n                name=\"Test User\",\n                role=UserRole.user,\n                is_active=True,\n                kyc_status=\"verified\"\n            )\n            db.add(test_user)\n            print(\"Test user created\")\n        \n        db.commit()\n        print(\"All users created successfully!\")\n        \n    except Exception as e:\n        print(f\"Error: {e}\")\n        db.rollback()\n    finally:\n        db.close()\n\nif __name__ == \"__main__\":\n    create_users()